sp_widget.client_script:
function($scope,$rootScope) {
	/* widget controller */
	var c = this;

	c.readOnlyFlag = true;
	c.showEditButtonFlag= true;
	c.showSaveandCancelButtonFlag = false;

	c.ShowSaveandCancel = function(visibleflag){
		c.readOnlyFlag = !visibleflag;	
		c.showEditButtonFlag= !visibleflag;
		c.showSaveandCancelButtonFlag= visibleflag;
		$rootScope.ignoreClicks = visibleflag;
	}

	$rootScope.$on('selectNote', function(event,data) {
		c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID
		}).then(function(r) {
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.noteID = r.data.note.sys_id;
		});
	});

	c.updateNote = function(updateType) {
		c.ShowSaveandCancel(false);
		if(updateType=='save')
		{
			c.server.get({
				action: 'updateNote',
				noteID: c.data.noteID,
				noteBody: c.data.note,
				noteTitle: c.data.title
			}).then(function(r) {
			});
		}
		else if (updateType=='cancel') { 
			//do nothing
			c.server.get({
				action: 'getNote',
				noteID: $rootScope.noteID
			}).then(function(r) {
				c.data.title = r.data.note.title;
				c.data.note = r.data.note.note;
				c.data.noteID = r.data.note.sys_id;
			});	
		}
	}
}

sp_widget.script:
(function() {
	/* populate the 'data' object */

	if (input && input.noteID) {
		var note = new GlideRecord('x_snc_createnotes_note');
		if (note.get(input.noteID)) {
			if (input.action == 'getNote') {
				data.note = {};
				$sp.getRecordValues(data.note, note, "title, note, sys_id");
			}
			else if (input.action == 'updateNote') { 
				note.title = input.noteTitle; 
				note.note = input.noteBody; 
				note.update(); 
			}
		}
	}	
})();
